{
  "key": "COMMISSION_SIGNATURE_POLICY",
  "name": "Pol√≠tica de Firma para Comisiones",
  "description": "Define si se requiere firma para cargar comisiones de especialistas",
  "type": "CONFIGURATION",
  "defaultValue": {
    "require_signature_for_commissions": true,
    "signature_threshold_amount": 0,
    "allow_digital_signature": false,
    "require_specialist_approval": true,
    "notification_settings": {
      "notify_on_unsigned_commission": true,
      "send_weekly_summary": true
    }
  },
  "category": "SERVICE_POLICY",
  "allowCustomization": true
}

{
  "key": "CLIENT_PRIVACY_POLICY",
  "name": "Pol√≠tica de Privacidad de Clientes",
  "description": "Define qu√© informaci√≥n personal de clientes pueden ver las especialistas",
  "type": "CONFIGURATION",
  "defaultValue": {
    "specialist_can_see_personal_data": true,
    "hide_contact_info": false,
    "hide_payment_history": true,
    "hide_medical_history": false,
    "show_only_relevant_services": true,
    "access_restrictions": {
      "limit_to_own_clients": false,
      "require_business_owner_approval": false,
      "log_data_access": true
    }
  },
  "category": "SERVICE_POLICY",
  "allowCustomization": true
}

{
  "key": "TRANSFER_PROOF_POLICY",
  "name": "Pol√≠tica de Comprobantes de Transferencia",
  "description": "Define si se requiere comprobante de transferencia para agendar citas",
  "type": "CONFIGURATION",
  "defaultValue": {
    "require_transfer_proof_for_booking": true,
    "auto_verify_known_accounts": false,
    "grace_period_hours": 2,
    "minimum_amount_requiring_proof": 10000,
    "verification_settings": {
      "manual_verification_required": true,
      "auto_approve_after_hours": 24,
      "notify_admin_on_pending": true
    },
    "exceptions": {
      "vip_clients_exempt": false,
      "recurring_clients_exempt": true
    }
  },
  "category": "PAYMENT_POLICY",
  "allowCustomization": true
}

{
  "key": "EXPENSE_RECEIPT_POLICY",
  "name": "Pol√≠tica de Gastos sin Comprobante",
  "description": "Define si el business puede crear gastos sin comprobante",
  "type": "CONFIGURATION",
  "defaultValue": {
    "allow_expenses_without_receipt": false,
    "max_amount_without_receipt": 10000,
    "require_justification": true,
    "require_approval": true,
    "approval_settings": {
      "approval_required_above_amount": 5000,
      "approver_role": "BUSINESS_OWNER",
      "auto_approve_recurring": false
    },
    "tracking_settings": {
      "flag_for_review": true,
      "monthly_limit_without_receipt": 50000,
      "notify_accountant": true
    }
  },
  "category": "GENERAL",
  "allowCustomization": true
}

